mapper.insert:
insert into REPLY_FORM(
	Board_No, Reply_Id, Content, Reg_Date, member_Id
) values (
	#{no}, #{replyId}, #{content}, #{regDate}, #{memberId}
)

mapper.update:
update REPLY_FORM
   set content = #{content}
 where board_no = #{board_no}

mapper.select:
select nick_name
  from member
 where member_id = #{memberId}
